import axios from 'axios';

export const api = axios.create({
  baseURL: process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000/api',
});

import { create } from 'zustand';
import Cookies from 'js-cookie';
import { api } from '@/lib/services/http';
import { AxiosError } from 'axios';

export type User = {
  id: string;
  email: string;
  first_name: string;
  last_name: string;
  phone: string;
  role: string;
  name: string;
};

type RegisterParams = {
  email: string;
  password: string;
  password_confirm: string;
  first_name: string;
  last_name: string;
  phone?: string;
  captcha_token?: string;
};

type AuthState = {
  user: User | null;
  accessToken: string | null;
  isAuthenticated: boolean;
  justLoggedIn: boolean;
  hydrated: boolean;
  login: (email: string, password: string, captchaToken?: string) => Promise<{ success: boolean; error?: string }>;
  register: (params: RegisterParams) => Promise<{ success: boolean; error?: string }>;
  logout: () => void;
  hydrate: () => void;
  clearJustLoggedIn: () => void;
};

type LoginResponse = {
  user: {
    id: number;
    email: string;
    first_name: string;
    last_name: string;
    phone: string;
    role: string;
  };
  tokens: {
    access: string;
    refresh: string;
  };
};

type ProfileResponse = {
  user: LoginResponse['user'];
};

function clearAuthCookies() {
  Cookies.remove('kore_token');
  Cookies.remove('kore_refresh');
  Cookies.remove('kore_user');
}

function mapUser(raw: LoginResponse['user']): User {
  const first = raw.first_name || '';
  const last = raw.last_name || '';
  return {
    id: String(raw.id),
    email: raw.email,
    first_name: first,
    last_name: last,
    phone: raw.phone || '',
    role: raw.role,
    name: [first, last].filter(Boolean).join(' ') || raw.email,
  };
}

export const useAuthStore = create<AuthState>((set, get) => ({
  user: null,
  accessToken: null,
  isAuthenticated: false,
  justLoggedIn: false,
  hydrated: false,

  login: async (email: string, password: string, captchaToken?: string) => {
    try {
      const { data } = await api.post<LoginResponse>('/auth/login/', { email, password, captcha_token: captchaToken });

      const user = mapUser(data.user);
      const accessToken = data.tokens.access;

      Cookies.set('kore_token', accessToken, { expires: 7 });
      Cookies.set('kore_refresh', data.tokens.refresh, { expires: 7 });
      Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });

      if (typeof window !== 'undefined') {
        sessionStorage.removeItem('kore_reminder_dismissed');
      }

      set({ user, accessToken, isAuthenticated: true, justLoggedIn: true });
      return { success: true };
    } catch (err) {
      const axiosErr = err as AxiosError<Record<string, unknown>>;
      const detail = axiosErr.response?.data?.non_field_errors
        ?? axiosErr.response?.data?.detail;
      const message = Array.isArray(detail) ? detail[0] : detail;
      return {
        success: false,
        error: typeof message === 'string' ? message : 'Correo o contraseña incorrectos',
      };
    }
  },

  register: async (params: RegisterParams) => {
    try {
      const { data } = await api.post<LoginResponse>('/auth/register/', params);

      const user = mapUser(data.user);
      const accessToken = data.tokens.access;

      Cookies.set('kore_token', accessToken, { expires: 7 });
      Cookies.set('kore_refresh', data.tokens.refresh, { expires: 7 });
      Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });

      set({ user, accessToken, isAuthenticated: true });
      return { success: true };
    } catch (err) {
      const axiosErr = err as AxiosError<Record<string, unknown>>;
      const responseData = axiosErr.response?.data;
      let message = 'Error al registrar la cuenta';
      if (responseData) {
        const firstError = Object.values(responseData)[0];
        if (Array.isArray(firstError)) {
          message = firstError[0] as string;
        } else if (typeof firstError === 'string') {
          message = firstError;
        }
      }
      return { success: false, error: message };
    }
  },

  logout: () => {
    clearAuthCookies();
    if (typeof window !== 'undefined') {
      sessionStorage.removeItem('kore_reminder_dismissed');
    }
    set({ user: null, accessToken: null, isAuthenticated: false });
  },

  hydrate: () => {
    if (get().hydrated) {
      return;
    }

    const currentState = get();
    if (currentState.isAuthenticated && currentState.accessToken && currentState.user) {
      set({ hydrated: true });
      return;
    }

    const token = Cookies.get('kore_token');
    const userStr = Cookies.get('kore_user');

    if (!token || !userStr) {
      set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      return;
    }

    try {
      JSON.parse(userStr) as User;
    } catch {
      clearAuthCookies();
      set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      return;
    }

    set({ hydrated: false });

    void api.get<ProfileResponse>('/auth/profile/', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(({ data }) => {
        const user = mapUser(data.user);
        Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });
        set({ user, accessToken: token, isAuthenticated: true, hydrated: true });
      })
      .catch(() => {
        clearAuthCookies();
        set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      });
  },

  clearJustLoggedIn: () => {
    set({ justLoggedIn: false });
  },
}));

'use client';

import Image from 'next/image';
import Link from 'next/link';
import { usePathname } from 'next/navigation';
import { useEffect, useState } from 'react';
import { useAuthStore } from '@/lib/stores/authStore';

const navLinks = [
  { href: '/', label: 'Inicio' },
  { href: '/kore-brand', label: 'La Marca' },
  { href: '/programs', label: 'Programas' },
  { href: '/faq', label: 'FAQ' },
  { href: '/contact', label: 'Contacto' },
];

export default function Navbar() {
  const pathname = usePathname();
  const [mobileOpen, setMobileOpen] = useState(false);
  const { isAuthenticated, hydrate } = useAuthStore();

  useEffect(() => {
    hydrate();
  }, [hydrate]);

  const desktopCtaHref = isAuthenticated ? '/dashboard' : '/login';
  const desktopCtaLabel = isAuthenticated ? 'Mi sesión' : 'Iniciar sesión';

  const isActive = (href: string) =>
    href === '/' ? pathname === '/' : pathname.startsWith(href);

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-kore-cream/90 backdrop-blur-md border-b border-kore-gray-light/50">
      <div className="w-full px-6 md:px-10 lg:px-16 flex items-center justify-between h-20">
        {/* Logo */}
        <Link href="/" className="flex items-center gap-3 shrink-0">
          <Image
            src="/icons/kore-logo.webp"
            alt="KÓRE"
            width={44}
            height={44}
            className="rounded-full w-11 h-11"
          />
          <span className="font-heading text-xl font-semibold text-kore-wine-dark tracking-wide">
            KÓRE
          </span>
        </Link>

        {/* Desktop Links - Centered */}
        <ul className="hidden md:flex items-center gap-10 absolute left-1/2 -translate-x-1/2">
          {navLinks.map((link) => (
            <li key={link.href} className="relative">
              <Link
                href={link.href}
                className={`block py-2 font-medium text-sm tracking-wide uppercase transition-colors duration-200 ${
                  isActive(link.href)
                    ? 'text-kore-red'
                    : 'text-kore-gray-dark hover:text-kore-red'
                }`}
              >
                {link.label}
              </Link>
              {/* Active indicator bar */}
              <span
                className={`absolute bottom-0 left-0 h-0.5 bg-kore-red rounded-full transition-all duration-300 ${
                  isActive(link.href) ? 'w-full' : 'w-0'
                }`}
              />
            </li>
          ))}
        </ul>

        {/* CTA Desktop */}
        <div className="hidden md:block shrink-0">
          <Link
            href={desktopCtaHref}
            className="inline-flex items-center justify-center border-2 border-kore-red text-kore-red hover:bg-kore-red hover:text-white font-medium text-sm px-6 py-2.5 rounded-lg transition-colors duration-200"
          >
            {desktopCtaLabel}
          </Link>
        </div>

        {/* Mobile Menu Button */}
        <button
          onClick={() => setMobileOpen(!mobileOpen)}
          className="md:hidden flex flex-col justify-center items-center gap-1.5 w-10 h-10"
          aria-label="Menú"
        >
          <span
            className={`block w-6 h-0.5 bg-kore-gray-dark transition-all duration-300 ${
              mobileOpen ? 'rotate-45 translate-y-2' : ''
            }`}
          />
          <span
            className={`block w-6 h-0.5 bg-kore-gray-dark transition-all duration-300 ${
              mobileOpen ? 'opacity-0' : ''
            }`}
          />
          <span
            className={`block w-6 h-0.5 bg-kore-gray-dark transition-all duration-300 ${
              mobileOpen ? '-rotate-45 -translate-y-2' : ''
            }`}
          />
        </button>
      </div>

      {/* Mobile Menu */}
      <div
        data-testid="mobile-nav-menu"
        className={`md:hidden overflow-hidden transition-all duration-300 bg-kore-cream/95 backdrop-blur-md ${
          mobileOpen ? 'max-h-80 border-b border-kore-gray-light/50' : 'max-h-0'
        }`}
      >
        <ul className="flex flex-col px-6 py-4 gap-4">
          {navLinks.map((link) => (
            <li key={link.href}>
              <Link
                href={link.href}
                onClick={() => setMobileOpen(false)}
                className={`block font-medium text-base tracking-wide transition-colors duration-200 ${
                  isActive(link.href)
                    ? 'text-kore-red border-l-2 border-kore-red pl-3'
                    : 'text-kore-gray-dark hover:text-kore-red'
                }`}
              >
                {link.label}
              </Link>
            </li>
          ))}
          <li className="pt-2">
            <Link
              href={desktopCtaHref}
              onClick={() => setMobileOpen(false)}
              className="inline-flex items-center justify-center border-2 border-kore-red text-kore-red hover:bg-kore-red hover:text-white font-medium text-sm px-6 py-2.5 rounded-lg transition-colors duration-200"
            >
              {desktopCtaLabel}
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
}

