'use client';

import { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

export function useTextReveal(containerRef: React.RefObject<HTMLElement | null>) {
  useEffect(() => {
    if (!containerRef.current) return;

    const ctx = gsap.context(() => {
      const elements = containerRef.current!.querySelectorAll('[data-animate]');

      elements.forEach((el) => {
        const type = el.getAttribute('data-animate');
        const delay = parseFloat(el.getAttribute('data-delay') || '0');

        gsap.set(el, { opacity: 0, y: 30 });

        const config: gsap.TweenVars = {
          opacity: 1,
          y: 0,
          duration: 0.8,
          delay,
          ease: 'power3.out',
          scrollTrigger: {
            trigger: el,
            start: 'top 80%',
            toggleActions: 'play none none none',
          },
        };

        switch (type) {
          case 'fade-up':
            break;
          case 'fade-up-slow':
            config.duration = 1.2;
            config.y = 50;
            break;
          case 'fade-left':
            gsap.set(el, { opacity: 0, x: -40, y: 0 });
            config.y = 0;
            config.x = 0;
            break;
          case 'fade-right':
            gsap.set(el, { opacity: 0, x: 40, y: 0 });
            config.y = 0;
            config.x = 0;
            break;
          case 'scale-in':
            gsap.set(el, { opacity: 0, scale: 0.9, y: 0 });
            config.y = 0;
            config.scale = 1;
            break;
          case 'split-text':
            config.duration = 1;
            config.y = 40;
            config.ease = 'power4.out';
            break;
          case 'stagger-children':
            gsap.set(el, { opacity: 1, y: 0 });
            const children = el.children;
            gsap.set(children, { opacity: 0, y: 25 });
            gsap.to(children, {
              opacity: 1,
              y: 0,
              duration: 0.6,
              stagger: 0.1,
              delay,
              ease: 'power3.out',
              scrollTrigger: {
                trigger: el,
                start: 'top 80%',
                toggleActions: 'play none none none',
              },
            });
            return;
        }

        gsap.to(el, config);
      });
    }, containerRef);

    return () => ctx.revert();
  }, [containerRef]);
}

export function useHeroAnimation(containerRef: React.RefObject<HTMLElement | null>) {
  useEffect(() => {
    if (!containerRef.current) return;

    const ctx = gsap.context(() => {
      const tl = gsap.timeline({ defaults: { ease: 'power3.out' } });

      const badge = containerRef.current?.querySelector('[data-hero="badge"]');
      const heading = containerRef.current?.querySelector('[data-hero="heading"]');
      const subtitle = containerRef.current?.querySelector('[data-hero="subtitle"]');
      const body = containerRef.current?.querySelector('[data-hero="body"]');
      const cta = containerRef.current?.querySelector('[data-hero="cta"]');
      const stats = containerRef.current?.querySelector('[data-hero="stats"]');
      const image = containerRef.current?.querySelector('[data-hero="image"]');

      if (badge) tl.from(badge, { opacity: 0, y: 20, duration: 0.6, delay: 0.2 });
      if (heading) tl.from(heading, { opacity: 0, y: 40, duration: 0.8 }, badge ? '-=0.3' : undefined);
      if (subtitle) tl.from(subtitle, { opacity: 0, y: 30, duration: 0.7 }, '-=0.4');
      if (body) tl.from(body, { opacity: 0, y: 25, duration: 0.7 }, '-=0.4');
      if (cta) tl.from(cta, { opacity: 0, y: 20, duration: 0.6 }, '-=0.3');
      if (stats) tl.from(stats, { opacity: 0, y: 20, duration: 0.6 }, '-=0.3');
      if (image) tl.from(image, { opacity: 0, scale: 0.85, duration: 1.2, ease: 'power2.out' }, '-=0.8');
    }, containerRef);

    return () => ctx.revert();
  }, [containerRef]);
}
import { create } from 'zustand';
import Cookies from 'js-cookie';
import { api } from '@/lib/services/http';
import { AxiosError } from 'axios';
import { useAuthStore } from '@/lib/stores/authStore';

export type PackageDetail = {
  id: number;
  title: string;
  description: string;
  sessions_count: number;
  price: string;
  currency: string;
  validity_days: number;
  is_active: boolean;
};

type WompiConfig = {
  public_key: string;
  environment: string;
};

export type PaymentIntentResult = {
  id: number;
  reference: string;
  wompi_transaction_id: string;
  status: 'pending' | 'approved' | 'failed';
  amount: string;
  currency: string;
  package_title: string;
  checkout_access_token?: string;
  auto_login?: {
    access: string;
    refresh: string;
    user: {
      id: number;
      email: string;
      first_name: string;
      last_name: string;
      phone: string;
      role: string;
    };
  };
  created_at: string;
};

type PaymentStatus = 'idle' | 'processing' | 'polling' | 'success' | 'error';

type SignatureData = {
  signature: string;
  reference: string;
};

export type CheckoutPreparation = {
  reference: string;
  signature: string;
  amount_in_cents: number;
  currency: string;
  package_title: string;
  checkout_access_token?: string;
};

type CheckoutState = {
  package_: PackageDetail | null;
  wompiConfig: WompiConfig | null;
  loading: boolean;
  paymentStatus: PaymentStatus;
  intentResult: PaymentIntentResult | null;
  error: string;
  fetchPackage: (id: string) => Promise<void>;
  fetchWompiConfig: () => Promise<void>;
  generateSignature: (amountInCents: number, currency: string) => Promise<SignatureData | null>;
  prepareCheckout: (packageId: number, registrationToken?: string) => Promise<CheckoutPreparation | null>;
  purchaseSubscription: (packageId: number, cardToken: string, registrationToken?: string) => Promise<boolean>;
  pollIntentStatus: (reference: string, checkoutAccessToken?: string, transactionId?: string) => Promise<boolean>;
  reset: () => void;
};

const POLL_INTERVAL_MS = 2000;
const POLL_MAX_ATTEMPTS = 30;

function applyAutoLoginSession(autoLogin: NonNullable<PaymentIntentResult['auto_login']>) {
  const first = autoLogin.user.first_name || '';
  const last = autoLogin.user.last_name || '';
  const mappedUser = {
    id: String(autoLogin.user.id),
    email: autoLogin.user.email,
    first_name: first,
    last_name: last,
    phone: autoLogin.user.phone || '',
    role: autoLogin.user.role,
    name: [first, last].filter(Boolean).join(' ') || autoLogin.user.email,
  };

  Cookies.set('kore_token', autoLogin.access, { expires: 7 });
  Cookies.set('kore_refresh', autoLogin.refresh, { expires: 7 });
  Cookies.set('kore_user', JSON.stringify(mappedUser), { expires: 7 });

  useAuthStore.setState({
    user: mappedUser,
    accessToken: autoLogin.access,
    isAuthenticated: true,
    hydrated: true,
    justLoggedIn: true,
  });
}

export const useCheckoutStore = create<CheckoutState>((set, get) => ({
  package_: null,
  wompiConfig: null,
  loading: false,
  paymentStatus: 'idle',
  intentResult: null,
  error: '',

  fetchPackage: async (id: string) => {
    set({ loading: true, error: '' });
    try {
      const { data } = await api.get<PackageDetail>(`/packages/${id}/`);
      set({ package_: data, loading: false });
    } catch {
      set({ error: 'No se pudo cargar el paquete.', loading: false });
    }
  },

  fetchWompiConfig: async () => {
    try {
      const { data } = await api.get<WompiConfig>('/wompi/config/');
      if (!data?.public_key) {
        set({ wompiConfig: null, error: 'No se pudo cargar la configuración de pago.' });
        return;
      }
      set({ wompiConfig: data });
    } catch {
      set({ error: 'No se pudo cargar la configuración de pago.' });
    }
  },

  generateSignature: async (amountInCents: number, currency: string) => {
    try {
      const token = Cookies.get('kore_token');
      const reference = `kore-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
      const requestConfig = token
        ? { headers: { Authorization: `Bearer ${token}` } }
        : undefined;
      const { data } = await api.post<SignatureData>(
        '/wompi/generate-signature/',
        { reference, amount_in_cents: amountInCents, currency },
        requestConfig,
      );
      return data;
    } catch {
      set({ error: 'No se pudo generar la firma de pago.' });
      return null;
    }
  },

  prepareCheckout: async (packageId: number, registrationToken?: string) => {
    set({ paymentStatus: 'processing', error: '' });
    try {
      const token = Cookies.get('kore_token');
      const payload: { package_id: number; registration_token?: string } = {
        package_id: packageId,
      };
      if (registrationToken) {
        payload.registration_token = registrationToken;
      }
      const requestConfig = token
        ? { headers: { Authorization: `Bearer ${token}` } }
        : undefined;

      const { data } = await api.post<CheckoutPreparation>(
        '/subscriptions/prepare-checkout/',
        payload,
        requestConfig,
      );
      return data;
    } catch (err) {
      const axiosErr = err as AxiosError<{ detail?: string }>;
      const message = axiosErr.response?.data?.detail || 'No se pudo preparar el pago. Intenta de nuevo.';
      set({ paymentStatus: 'error', error: message });
      return null;
    }
  },

  purchaseSubscription: async (packageId: number, cardToken: string, registrationToken?: string) => {
    set({ paymentStatus: 'processing', error: '' });
    try {
      const token = Cookies.get('kore_token');
      const payload: { package_id: number; card_token: string; registration_token?: string } = {
        package_id: packageId,
        card_token: cardToken,
      };

      if (registrationToken) {
        payload.registration_token = registrationToken;
      }

      const requestConfig = token
        ? { headers: { Authorization: `Bearer ${token}` } }
        : undefined;

      const { data } = await api.post<PaymentIntentResult>(
        '/subscriptions/purchase/',
        payload,
        requestConfig,
      );
      set({ intentResult: data, paymentStatus: 'polling' });
      return await get().pollIntentStatus(data.reference, data.checkout_access_token);
    } catch (err) {
      const axiosErr = err as AxiosError<{ detail?: string }>;
      const message = axiosErr.response?.data?.detail || 'Error al procesar el pago. Intenta de nuevo.';
      set({ paymentStatus: 'error', error: message });
      return false;
    }
  },

  pollIntentStatus: async (reference: string, checkoutAccessToken?: string, transactionId?: string) => {
    const token = Cookies.get('kore_token');
    for (let attempt = 0; attempt < POLL_MAX_ATTEMPTS; attempt++) {
      await new Promise((r) => setTimeout(r, POLL_INTERVAL_MS));
      try {
        const params: Record<string, string> = {};
        if (!token && checkoutAccessToken) {
          params.access_token = checkoutAccessToken;
        }
        if (transactionId) {
          params.transaction_id = transactionId;
        }

        const requestConfig = token
          ? {
            headers: { Authorization: `Bearer ${token}` },
            params: Object.keys(params).length ? params : undefined,
          }
          : Object.keys(params).length
            ? { params }
            : undefined;

        const { data } = await api.get<PaymentIntentResult>(
          `/subscriptions/intent-status/${reference}/`,
          requestConfig,
        );
        set({ intentResult: data });

        if (data.status === 'approved') {
          if (data.auto_login) {
            applyAutoLoginSession(data.auto_login);
          }
          set({ paymentStatus: 'success' });
          return true;
        }
        if (data.status === 'failed') {
          set({ paymentStatus: 'error', error: 'El pago fue rechazado. Intenta con otro método de pago.' });
          return false;
        }
      } catch {
        // Ignore transient polling errors, keep trying
      }
    }
    set({ paymentStatus: 'error', error: 'El pago está tardando más de lo esperado. Revisa tu estado en unos minutos.' });
    return false;
  },

  reset: () => {
    set({
      package_: null,
      wompiConfig: null,
      loading: false,
      paymentStatus: 'idle',
      intentResult: null,
      error: '',
    });
  },
}));
'use client';

import Image from 'next/image';
import Link from 'next/link';
import { Suspense, useState, useRef, useEffect, useCallback } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { useHeroAnimation } from '@/app/composables/useScrollAnimations';
import { useAuthStore } from '@/lib/stores/authStore';
import { useCheckoutStore } from '@/lib/stores/checkoutStore';

const CHECKOUT_REGISTRATION_TOKEN_KEY = 'kore_checkout_registration_token';
const CHECKOUT_REGISTRATION_PACKAGE_KEY = 'kore_checkout_registration_package';

export default function CheckoutPage() {
  return (
    <Suspense fallback={null}>
      <CheckoutContent />
    </Suspense>
  );
}

declare global {
  interface Window {
    WidgetCheckout?: new (config: Record<string, unknown>) => {
      open: (cb: (result: {
        payment_source?: { token?: string; type?: string };
        transaction?: { id?: string };
      }) => void) => void;
    };
  }
}

function CheckoutContent() {
  const sectionRef = useRef<HTMLElement>(null);
  const router = useRouter();
  const searchParams = useSearchParams();
  const packageId = searchParams.get('package');

  const { isAuthenticated, hydrate, hydrated } = useAuthStore();
  const {
    package_: pkg,
    wompiConfig,
    loading,
    paymentStatus,
    intentResult,
    error,
    fetchPackage,
    fetchWompiConfig,
    prepareCheckout,
    pollIntentStatus,
    reset,
  } = useCheckoutStore();

  const prevPaymentStatusRef = useRef<string | null>(null);
  const [widgetLoaded, setWidgetLoaded] = useState(false);
  const [widgetError, setWidgetError] = useState(false);
  const [openingCheckout, setOpeningCheckout] = useState(false);
  const [registrationToken, setRegistrationToken] = useState<string | null>(() => {
    if (typeof window === 'undefined') return null;
    const token = sessionStorage.getItem(CHECKOUT_REGISTRATION_TOKEN_KEY);
    const tokenPkg = sessionStorage.getItem(CHECKOUT_REGISTRATION_PACKAGE_KEY);
    return (token && packageId && tokenPkg === packageId) ? token : null;
  });

  useHeroAnimation(sectionRef);

  useEffect(() => {
    hydrate();
  }, [hydrate]);

  useEffect(() => {
    if (!hydrated) {
      return;
    }

    if (isAuthenticated) {
      setRegistrationToken(null);
      return;
    }

    if (typeof window === 'undefined') {
      return;
    }

    const token = sessionStorage.getItem(CHECKOUT_REGISTRATION_TOKEN_KEY);
    const tokenPackage = sessionStorage.getItem(CHECKOUT_REGISTRATION_PACKAGE_KEY);
    if (token && packageId && tokenPackage === packageId) {
      setRegistrationToken(token);
      return;
    }

    const redirect = packageId ? `/register?package=${packageId}` : '/register';
    router.push(redirect);
  }, [hydrated, isAuthenticated, router, packageId]);

  useEffect(() => {
    if (typeof window === 'undefined') {
      return;
    }

    const prev = prevPaymentStatusRef.current;
    prevPaymentStatusRef.current = paymentStatus;

    if (prev !== null && prev !== paymentStatus && (paymentStatus === 'success' || paymentStatus === 'error')) {
      sessionStorage.removeItem(CHECKOUT_REGISTRATION_TOKEN_KEY);
      sessionStorage.removeItem(CHECKOUT_REGISTRATION_PACKAGE_KEY);
    }
  }, [paymentStatus]);

  const hasCheckoutAccess = isAuthenticated || Boolean(registrationToken);

  useEffect(() => {
    if (packageId && hasCheckoutAccess) {
      reset();
      fetchPackage(packageId);
      fetchWompiConfig();
    }
  }, [packageId, hasCheckoutAccess, fetchPackage, fetchWompiConfig, reset]);

  // Load Wompi widget script (programmatic mode — no data-public-key needed)
  useEffect(() => {
    if (!wompiConfig?.public_key) return;

    // Widget already available from a previous load
    if (window.WidgetCheckout) {
      setWidgetLoaded(true);
      return;
    }

    const existingScript = document.getElementById('wompi-widget-script');
    if (existingScript) {
      // Script tag exists but WidgetCheckout not yet available — poll for it
      const interval = setInterval(() => {
        if (window.WidgetCheckout) {
          clearInterval(interval);
          setWidgetLoaded(true);
          setWidgetError(false);
        }
      }, 200);
      const timeout = setTimeout(() => {
        clearInterval(interval);
        if (!window.WidgetCheckout) setWidgetError(true);
      }, 15000);
      return () => {
        clearInterval(interval);
        clearTimeout(timeout);
      };
    }

    const script = document.createElement('script');
    script.id = 'wompi-widget-script';
    script.src = 'https://checkout.wompi.co/widget.js';
    script.async = true;

    const timeout = setTimeout(() => {
      setWidgetError(true);
    }, 15000);

    script.onload = () => {
      clearTimeout(timeout);
      setWidgetLoaded(true);
      setWidgetError(false);
    };

    script.onerror = () => {
      clearTimeout(timeout);
      setWidgetError(true);
    };

    document.body.appendChild(script);

    return () => {
      clearTimeout(timeout);
    };
  }, [wompiConfig?.public_key]);

  // Clean up widget on unmount to prevent stale state across navigations / HMR
  useEffect(() => {
    return () => {
      const script = document.getElementById('wompi-widget-script');
      if (script) script.remove();
    };
  }, []);

  const handleCheckout = useCallback(async () => {
    if (!wompiConfig || !pkg || !window.WidgetCheckout) return;
    setOpeningCheckout(true);

    const preparation = await prepareCheckout(
      pkg.id,
      isAuthenticated ? undefined : (registrationToken || undefined),
    );
    if (!preparation) {
      setOpeningCheckout(false);
      return;
    }

    let widgetCallbackFired = false;
    const widgetTimeout = setTimeout(() => {
      if (!widgetCallbackFired) {
        useCheckoutStore.setState({
          paymentStatus: 'error',
          error: 'La pasarela de pago no respondió. Recarga la página e intenta de nuevo.',
        });
        setOpeningCheckout(false);
      }
    }, 60000);

    const checkout = new window.WidgetCheckout({
      publicKey: wompiConfig.public_key,
      currency: preparation.currency,
      amountInCents: preparation.amount_in_cents,
      reference: preparation.reference,
      signature: { integrity: preparation.signature },
    });

    checkout.open(async (result) => {
      widgetCallbackFired = true;
      clearTimeout(widgetTimeout);
      const transactionId = result?.transaction?.id;
      if (transactionId) {
        useCheckoutStore.setState({ paymentStatus: 'polling', error: '' });
        await pollIntentStatus(preparation.reference, preparation.checkout_access_token, transactionId);
      } else {
        useCheckoutStore.setState({
          paymentStatus: 'error',
          error: 'No se pudo completar el pago. Intenta de nuevo.',
        });
      }
      setOpeningCheckout(false);
    });
  }, [
    wompiConfig,
    pkg,
    prepareCheckout,
    pollIntentStatus,
    isAuthenticated,
    registrationToken,
  ]);

  const formatPrice = (price: string) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'COP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(parseFloat(price));
  };

  const isProcessing = paymentStatus === 'processing' || paymentStatus === 'polling' || openingCheckout;

  if (!hydrated) {
    return (
      <section className="min-h-screen bg-kore-cream flex items-center justify-center">
        <div className="animate-spin h-8 w-8 border-2 border-kore-red border-t-transparent rounded-full" />
      </section>
    );
  }

  if (!hasCheckoutAccess) return null;

  if (paymentStatus === 'success' && intentResult) {
    return (
      <section ref={sectionRef} className="min-h-screen bg-kore-cream flex items-center justify-center relative overflow-hidden">
        <div className="absolute -right-40 -bottom-40 w-[600px] h-[600px] lg:w-[800px] lg:h-[800px] opacity-[0.04] pointer-events-none select-none">
          <Image
            src="/images/flower.webp"
            alt=""
            fill
            sizes="(max-width: 1024px) 600px, 800px"
            className="object-contain"
            aria-hidden="true"
          />
        </div>
        <div className="w-full max-w-lg mx-auto px-6 py-16">
          <div data-hero="badge" className="text-center mb-10">
            <div className="w-16 h-16 mx-auto mb-4 rounded-full bg-green-100 flex items-center justify-center">
              <svg className="w-8 h-8 text-green-600" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" d="M4.5 12.75l6 6 9-13.5" />
              </svg>
            </div>
            <h1 className="font-heading text-3xl font-semibold text-kore-gray-dark">
              ¡Pago exitoso!
            </h1>
            <p className="text-kore-gray-dark/50 text-sm mt-2">
              Tu suscripción ha sido activada
            </p>
          </div>
          <div data-hero="body" className="bg-white/60 backdrop-blur-sm rounded-2xl p-8 shadow-sm border border-kore-gray-light/50 space-y-4">
            {pkg && (
              <>
                <div className="flex justify-between text-sm">
                  <span className="text-kore-gray-dark/60">Programa</span>
                  <span className="font-medium text-kore-gray-dark">{intentResult.package_title}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-kore-gray-dark/60">Sesiones</span>
                  <span className="font-medium text-kore-gray-dark">{pkg.sessions_count}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-kore-gray-dark/60">Monto</span>
                  <span className="font-medium text-kore-gray-dark">{formatPrice(intentResult.amount)}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-kore-gray-dark/60">Vigencia</span>
                  <span className="font-medium text-kore-gray-dark">{pkg.validity_days} días</span>
                </div>
              </>
            )}
            <div className="pt-4">
              <Link
                href="/dashboard"
                className="w-full inline-flex items-center justify-center bg-kore-red hover:bg-kore-red-dark text-white font-medium py-3.5 rounded-lg transition-colors duration-200 text-sm tracking-wide cursor-pointer"
              >
                Ir a mi dashboard
              </Link>
            </div>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section ref={sectionRef} className="min-h-screen bg-kore-cream flex items-center justify-center relative overflow-hidden">
      <div className="absolute -right-40 -bottom-40 w-[600px] h-[600px] lg:w-[800px] lg:h-[800px] opacity-[0.04] pointer-events-none select-none">
        <Image
          src="/images/flower.webp"
          alt=""
          fill
          sizes="(max-width: 1024px) 600px, 800px"
          className="object-contain"
          aria-hidden="true"
        />
      </div>

      <div className="w-full max-w-lg mx-auto px-6 py-16">
        {/* Header */}
        <div data-hero="badge" className="text-center mb-10">
          <Link href="/" className="cursor-pointer">
            <span className="font-heading text-5xl font-semibold text-kore-gray-dark tracking-tight">
              KÓRE
            </span>
          </Link>
          <p data-hero="subtitle" className="text-kore-gray-dark/50 text-sm mt-2 tracking-wide">
            Completa tu suscripción
          </p>
        </div>

        {/* Content */}
        <div data-hero="body" className="bg-white/60 backdrop-blur-sm rounded-2xl p-8 shadow-sm border border-kore-gray-light/50">
          {loading ? (
            <div className="flex items-center justify-center py-12">
              <svg className="animate-spin h-8 w-8 text-kore-red" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z" />
              </svg>
            </div>
          ) : !pkg ? (
            <div className="text-center py-12">
              <p className="text-kore-gray-dark/50 text-sm">
                {error || 'Paquete no encontrado'}
              </p>
              <Link
                href="/programs"
                className="text-kore-red text-sm mt-2 inline-block hover:text-kore-red-dark transition-colors cursor-pointer"
              >
                Ver programas disponibles
              </Link>
            </div>
          ) : (
            <div className="space-y-6">
              {/* Package summary */}
              <div>
                <h2 className="font-heading text-xl font-semibold text-kore-gray-dark mb-4">
                  Resumen del programa
                </h2>
                <div className="space-y-3">
                  <div className="flex justify-between text-sm">
                    <span className="text-kore-gray-dark/60">Programa</span>
                    <span className="font-medium text-kore-gray-dark">{pkg.title}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-kore-gray-dark/60">Sesiones incluidas</span>
                    <span className="font-medium text-kore-gray-dark">{pkg.sessions_count}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-kore-gray-dark/60">Vigencia</span>
                    <span className="font-medium text-kore-gray-dark">{pkg.validity_days} días</span>
                  </div>
                  <div className="h-px bg-kore-gray-light/60 my-2" />
                  <div className="flex justify-between">
                    <span className="text-sm font-medium text-kore-gray-dark">Total</span>
                    <span className="text-lg font-semibold text-kore-red">{formatPrice(pkg.price)}</span>
                  </div>
                  <p className="text-xs text-kore-gray-dark/40">
                    El cobro automático aplica solo con tarjeta. Con otros métodos, deberás renovar manualmente cada {pkg.validity_days} días.
                  </p>
                </div>
              </div>

              {/* Error */}
              {error && (
                <div className="bg-kore-red/5 border border-kore-red/20 rounded-lg px-4 py-3">
                  <p className="text-sm text-kore-red">{error}</p>
                </div>
              )}

              {/* Payment button */}
              <div>
                {widgetError ? (
                  <div className="text-center py-4">
                    <div className="bg-kore-red/5 border border-kore-red/20 rounded-lg px-4 py-3 mb-4">
                      <p className="text-sm text-kore-red">
                        No se pudo cargar la pasarela de pago. Por favor, recarga la página o intenta más tarde.
                      </p>
                    </div>
                    <button
                      onClick={() => window.location.reload()}
                      className="text-kore-red text-sm hover:text-kore-red-dark transition-colors cursor-pointer"
                    >
                      Recargar página
                    </button>
                  </div>
                ) : (
                  <>
                    <button
                      onClick={handleCheckout}
                      disabled={!widgetLoaded || isProcessing}
                      className="w-full bg-kore-red hover:bg-kore-red-dark text-white font-medium py-3.5 rounded-lg transition-colors duration-200 text-sm tracking-wide disabled:opacity-60 disabled:cursor-not-allowed cursor-pointer"
                    >
                      {isProcessing ? (
                        <span className="inline-flex items-center gap-2">
                          <svg className="animate-spin h-4 w-4" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z" />
                          </svg>
                          {paymentStatus === 'polling' ? 'Verificando pago...' : 'Procesando pago...'}
                        </span>
                      ) : !widgetLoaded ? (
                        <span className="inline-flex items-center gap-2">
                          <svg className="animate-spin h-4 w-4" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z" />
                          </svg>
                          Cargando pasarela de pago...
                        </span>
                      ) : (
                        `Pagar ${formatPrice(pkg.price)}`
                      )}
                    </button>
                    <p className="text-center text-xs text-kore-gray-dark/30 mt-3">
                      Pago seguro procesado por Wompi
                    </p>
                    <p className="text-center text-xs text-kore-gray-dark/40 mt-2">
                      Métodos disponibles: Tarjeta (recurrente), Nequi, PSE y Bancolombia Transfer (no recurrentes).
                    </p>
                  </>
                )}
              </div>
            </div>
          )}
        </div>

        {/* Back link */}
        <p data-hero="cta" className="text-center text-xs text-kore-gray-dark/30 mt-8">
          <Link
            href="/programs"
            className="text-kore-red hover:text-kore-red-dark transition-colors cursor-pointer"
          >
            ← Volver a programas
          </Link>
        </p>
      </div>
    </section>
  );
}
