import axios from 'axios';

export const api = axios.create({
  baseURL: process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000/api',
});

import { create } from 'zustand';
import Cookies from 'js-cookie';
import { api } from '@/lib/services/http';
import { AxiosError } from 'axios';

export type User = {
  id: string;
  email: string;
  first_name: string;
  last_name: string;
  phone: string;
  role: string;
  name: string;
};

type RegisterParams = {
  email: string;
  password: string;
  password_confirm: string;
  first_name: string;
  last_name: string;
  phone?: string;
  captcha_token?: string;
};

type AuthState = {
  user: User | null;
  accessToken: string | null;
  isAuthenticated: boolean;
  justLoggedIn: boolean;
  hydrated: boolean;
  login: (email: string, password: string, captchaToken?: string) => Promise<{ success: boolean; error?: string }>;
  register: (params: RegisterParams) => Promise<{ success: boolean; error?: string }>;
  logout: () => void;
  hydrate: () => void;
  clearJustLoggedIn: () => void;
};

type LoginResponse = {
  user: {
    id: number;
    email: string;
    first_name: string;
    last_name: string;
    phone: string;
    role: string;
  };
  tokens: {
    access: string;
    refresh: string;
  };
};

type ProfileResponse = {
  user: LoginResponse['user'];
};

function clearAuthCookies() {
  Cookies.remove('kore_token');
  Cookies.remove('kore_refresh');
  Cookies.remove('kore_user');
}

function mapUser(raw: LoginResponse['user']): User {
  const first = raw.first_name || '';
  const last = raw.last_name || '';
  return {
    id: String(raw.id),
    email: raw.email,
    first_name: first,
    last_name: last,
    phone: raw.phone || '',
    role: raw.role,
    name: [first, last].filter(Boolean).join(' ') || raw.email,
  };
}

export const useAuthStore = create<AuthState>((set, get) => ({
  user: null,
  accessToken: null,
  isAuthenticated: false,
  justLoggedIn: false,
  hydrated: false,

  login: async (email: string, password: string, captchaToken?: string) => {
    try {
      const { data } = await api.post<LoginResponse>('/auth/login/', { email, password, captcha_token: captchaToken });

      const user = mapUser(data.user);
      const accessToken = data.tokens.access;

      Cookies.set('kore_token', accessToken, { expires: 7 });
      Cookies.set('kore_refresh', data.tokens.refresh, { expires: 7 });
      Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });

      if (typeof window !== 'undefined') {
        sessionStorage.removeItem('kore_reminder_dismissed');
      }

      set({ user, accessToken, isAuthenticated: true, justLoggedIn: true });
      return { success: true };
    } catch (err) {
      const axiosErr = err as AxiosError<Record<string, unknown>>;
      const detail = axiosErr.response?.data?.non_field_errors
        ?? axiosErr.response?.data?.detail;
      const message = Array.isArray(detail) ? detail[0] : detail;
      return {
        success: false,
        error: typeof message === 'string' ? message : 'Correo o contraseña incorrectos',
      };
    }
  },

  register: async (params: RegisterParams) => {
    try {
      const { data } = await api.post<LoginResponse>('/auth/register/', params);

      const user = mapUser(data.user);
      const accessToken = data.tokens.access;

      Cookies.set('kore_token', accessToken, { expires: 7 });
      Cookies.set('kore_refresh', data.tokens.refresh, { expires: 7 });
      Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });

      set({ user, accessToken, isAuthenticated: true });
      return { success: true };
    } catch (err) {
      const axiosErr = err as AxiosError<Record<string, unknown>>;
      const responseData = axiosErr.response?.data;
      let message = 'Error al registrar la cuenta';
      if (responseData) {
        const firstError = Object.values(responseData)[0];
        if (Array.isArray(firstError)) {
          message = firstError[0] as string;
        } else if (typeof firstError === 'string') {
          message = firstError;
        }
      }
      return { success: false, error: message };
    }
  },

  logout: () => {
    clearAuthCookies();
    if (typeof window !== 'undefined') {
      sessionStorage.removeItem('kore_reminder_dismissed');
    }
    set({ user: null, accessToken: null, isAuthenticated: false });
  },

  hydrate: () => {
    if (get().hydrated) {
      return;
    }

    const currentState = get();
    if (currentState.isAuthenticated && currentState.accessToken && currentState.user) {
      set({ hydrated: true });
      return;
    }

    const token = Cookies.get('kore_token');
    const userStr = Cookies.get('kore_user');

    if (!token || !userStr) {
      set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      return;
    }

    try {
      JSON.parse(userStr) as User;
    } catch {
      clearAuthCookies();
      set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      return;
    }

    set({ hydrated: false });

    void api.get<ProfileResponse>('/auth/profile/', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(({ data }) => {
        const user = mapUser(data.user);
        Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });
        set({ user, accessToken: token, isAuthenticated: true, hydrated: true });
      })
      .catch(() => {
        clearAuthCookies();
        set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      });
  },

  clearJustLoggedIn: () => {
    set({ justLoggedIn: false });
  },
}));

/**
 * Centralized application constants.
 *
 * WhatsApp number should match the business contact configured in SiteSettings.
 * Update WHATSAPP_NUMBER when the real business number is provided.
 */

export const WHATSAPP_NUMBER = '+573014645272';
export const WHATSAPP_URL = `https://wa.me/${WHATSAPP_NUMBER}`;

'use client';

import { useState, useEffect } from 'react';
import Link from 'next/link';
import { usePathname, useRouter } from 'next/navigation';
import { useAuthStore } from '@/lib/stores/authStore';
import { WHATSAPP_URL } from '@/lib/constants';

const navItems = [
  {
    label: 'Inicio',
    href: '/dashboard',
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" d="M2.25 12l8.954-8.955a1.126 1.126 0 011.591 0L21.75 12M4.5 9.75v10.125c0 .621.504 1.125 1.125 1.125H9.75v-4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125V21h4.125c.621 0 1.125-.504 1.125-1.125V9.75M8.25 21h8.25" />
      </svg>
    ),
  },
  {
    label: 'Agendar Sesión',
    href: '/book-session',
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" d="M6.75 3v2.25M17.25 3v2.25M3 18.75V7.5a2.25 2.25 0 012.25-2.25h13.5A2.25 2.25 0 0121 7.5v11.25m-18 0A2.25 2.25 0 005.25 21h13.5A2.25 2.25 0 0021 18.75m-18 0v-7.5A2.25 2.25 0 015.25 9h13.5A2.25 2.25 0 0121 11.25v7.5" />
      </svg>
    ),
  },
  {
    label: 'Mis Programas',
    href: '/my-programs',
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" d="M9 12h3.75M9 15h3.75M9 18h3.75m3 .75H18a2.25 2.25 0 002.25-2.25V6.108c0-1.135-.845-2.098-1.976-2.192a48.424 48.424 0 00-1.123-.08m-5.801 0c-.065.21-.1.433-.1.664 0 .414.336.75.75.75h4.5a.75.75 0 00.75-.75 2.25 2.25 0 00-.1-.664m-5.8 0A2.251 2.251 0 0113.5 2.25H15a2.25 2.25 0 012.15 1.586m-5.8 0c-.376.023-.75.05-1.124.08C9.095 4.01 8.25 4.973 8.25 6.108V8.25m0 0H4.875c-.621 0-1.125.504-1.125 1.125v11.25c0 .621.504 1.125 1.125 1.125h9.75c.621 0 1.125-.504 1.125-1.125V9.375c0-.621-.504-1.125-1.125-1.125H8.25zM6.75 12h.008v.008H6.75V12zm0 3h.008v.008H6.75V15zm0 3h.008v.008H6.75V18z" />
      </svg>
    ),
  },
  {
    label: 'Mi Suscripción',
    href: '/subscription',
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" d="M2.25 8.25h19.5M2.25 9h19.5m-16.5 5.25h6m-6 2.25h3m-3.75 3h15a2.25 2.25 0 002.25-2.25V6.75A2.25 2.25 0 0019.5 4.5h-15a2.25 2.25 0 00-2.25 2.25v10.5A2.25 2.25 0 004.5 19.5z" />
      </svg>
    ),
  },
];

export default function Sidebar() {
  const pathname = usePathname();
  const router = useRouter();
  const { user, logout } = useAuthStore();
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    setIsOpen(false);
  }, [pathname]);

  const handleLogout = () => {
    logout();
    router.push('/');
  };

  return (
    <>
      {/* Mobile hamburger button */}
      <button
        onClick={() => setIsOpen(true)}
        className="fixed top-4 left-4 z-50 lg:hidden p-2 rounded-xl bg-white/80 backdrop-blur-sm border border-kore-gray-light/40 text-kore-gray-dark/60 hover:text-kore-gray-dark transition-colors"
        aria-label="Abrir menú"
      >
        <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5" />
        </svg>
      </button>

      {/* Backdrop overlay for mobile */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/30 backdrop-blur-sm z-40 lg:hidden cursor-pointer"
          onClick={() => setIsOpen(false)}
        />
      )}

      <aside className={`fixed left-0 top-0 h-screen w-64 bg-white border-r border-kore-gray-light/40 flex flex-col z-50 transition-transform duration-300 ease-in-out ${
        isOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0`}>
      {/* Logo */}
      <div className="px-6 pt-8 pb-6 flex items-center justify-between">
        <Link href="/dashboard">
          <span className="font-heading text-2xl font-semibold text-kore-gray-dark tracking-tight">
            KÓRE
          </span>
        </Link>
        <button
          onClick={() => setIsOpen(false)}
          className="lg:hidden p-1 rounded-lg text-kore-gray-dark/40 hover:text-kore-gray-dark transition-colors"
          aria-label="Cerrar menú"
        >
          <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>
      </div>

      {/* User Info */}
      {user && (
        <div className="px-6 pb-6 mb-2">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 rounded-full bg-kore-red/10 flex items-center justify-center flex-shrink-0">
              <span className="font-heading text-sm font-semibold text-kore-red">
                {user.name.charAt(0)}
              </span>
            </div>
            <div className="min-w-0">
              <p className="text-sm font-medium text-kore-gray-dark truncate">{user.name}</p>
              <p className="text-xs text-kore-gray-dark/40 truncate">{user.email}</p>
            </div>
          </div>
        </div>
      )}

      {/* Navigation */}
      <nav className="flex-1 px-3">
        <ul className="space-y-1">
          {navItems.map((item) => {
            const isActive = pathname === item.href || (item.href !== '/dashboard' && pathname.startsWith(item.href));
            return (
              <li key={item.href}>
                <Link
                  href={item.href}
                  className={`flex items-center gap-3 px-4 py-3 rounded-xl text-sm font-medium transition-all duration-200 ${
                    isActive
                      ? 'bg-kore-red/10 text-kore-red'
                      : 'text-kore-gray-dark/50 hover:bg-kore-cream hover:text-kore-gray-dark'
                  }`}
                >
                  {item.icon}
                  {item.label}
                </Link>
              </li>
            );
          })}
        </ul>
      </nav>

      {/* Bottom Section */}
      <div className="px-3 pb-6 space-y-1">
        <a
          href={WHATSAPP_URL}
          target="_blank"
          rel="noopener noreferrer"
          className="flex items-center gap-3 px-4 py-3 rounded-xl text-sm text-kore-gray-dark/40 hover:bg-kore-cream hover:text-kore-gray-dark transition-all duration-200"
        >
          <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" d="M8.625 9.75a.375.375 0 11-.75 0 .375.375 0 01.75 0zm0 0H8.25m4.125 0a.375.375 0 11-.75 0 .375.375 0 01.75 0zm0 0H12m4.125 0a.375.375 0 11-.75 0 .375.375 0 01.75 0zm0 0h-.375m-13.5 3.01c0 1.6 1.123 2.994 2.707 3.227 1.087.16 2.185.283 3.293.369V21l4.184-4.183a1.14 1.14 0 01.778-.332 48.294 48.294 0 005.83-.498c1.585-.233 2.708-1.626 2.708-3.228V6.741c0-1.602-1.123-2.995-2.707-3.228A48.394 48.394 0 0012 3c-2.392 0-4.744.175-7.043.513C3.373 3.746 2.25 5.14 2.25 6.741v6.018z" />
          </svg>
          Soporte
        </a>
        <button
          onClick={handleLogout}
          className="w-full flex items-center gap-3 px-4 py-3 rounded-xl text-sm text-kore-gray-dark/40 hover:bg-kore-red/5 hover:text-kore-red transition-all duration-200"
        >
          <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 9V5.25A2.25 2.25 0 0013.5 3h-6a2.25 2.25 0 00-2.25 2.25v13.5A2.25 2.25 0 007.5 21h6a2.25 2.25 0 002.25-2.25V15m3 0l3-3m0 0l-3-3m3 3H9" />
          </svg>
          Cerrar sesión
        </button>
      </div>
    </aside>
    </>
  );
}

'use client';

import { useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useAuthStore } from '@/lib/stores/authStore';
import Sidebar from '@/app/components/layouts/Sidebar';

export default function AppLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  const router = useRouter();
  const { isAuthenticated, hydrate, hydrated } = useAuthStore();

  useEffect(() => {
    hydrate();
  }, [hydrate]);

  useEffect(() => {
    if (hydrated && !isAuthenticated) {
      router.push('/login');
    }
  }, [hydrated, isAuthenticated, router]);

  return (
    <div className="min-h-screen bg-kore-cream">
      <Sidebar />
      <main className="lg:ml-64">
        {children}
      </main>
    </div>
  );
}

import axios from 'axios';

export const api = axios.create({
  baseURL: process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000/api',
});
import { create } from 'zustand';
import Cookies from 'js-cookie';
import { api } from '@/lib/services/http';
import { AxiosError } from 'axios';

export type User = {
  id: string;
  email: string;
  first_name: string;
  last_name: string;
  phone: string;
  role: string;
  name: string;
};

type RegisterParams = {
  email: string;
  password: string;
  password_confirm: string;
  first_name: string;
  last_name: string;
  phone?: string;
  captcha_token?: string;
};

type AuthState = {
  user: User | null;
  accessToken: string | null;
  isAuthenticated: boolean;
  justLoggedIn: boolean;
  hydrated: boolean;
  login: (email: string, password: string, captchaToken?: string) => Promise<{ success: boolean; error?: string }>;
  register: (params: RegisterParams) => Promise<{ success: boolean; error?: string }>;
  logout: () => void;
  hydrate: () => void;
  clearJustLoggedIn: () => void;
};

type LoginResponse = {
  user: {
    id: number;
    email: string;
    first_name: string;
    last_name: string;
    phone: string;
    role: string;
  };
  tokens: {
    access: string;
    refresh: string;
  };
};

type ProfileResponse = {
  user: LoginResponse['user'];
};

function clearAuthCookies() {
  Cookies.remove('kore_token');
  Cookies.remove('kore_refresh');
  Cookies.remove('kore_user');
}

function mapUser(raw: LoginResponse['user']): User {
  const first = raw.first_name || '';
  const last = raw.last_name || '';
  return {
    id: String(raw.id),
    email: raw.email,
    first_name: first,
    last_name: last,
    phone: raw.phone || '',
    role: raw.role,
    name: [first, last].filter(Boolean).join(' ') || raw.email,
  };
}

export const useAuthStore = create<AuthState>((set, get) => ({
  user: null,
  accessToken: null,
  isAuthenticated: false,
  justLoggedIn: false,
  hydrated: false,

  login: async (email: string, password: string, captchaToken?: string) => {
    try {
      const { data } = await api.post<LoginResponse>('/auth/login/', { email, password, captcha_token: captchaToken });

      const user = mapUser(data.user);
      const accessToken = data.tokens.access;

      Cookies.set('kore_token', accessToken, { expires: 7 });
      Cookies.set('kore_refresh', data.tokens.refresh, { expires: 7 });
      Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });

      if (typeof window !== 'undefined') {
        sessionStorage.removeItem('kore_reminder_dismissed');
      }

      set({ user, accessToken, isAuthenticated: true, justLoggedIn: true });
      return { success: true };
    } catch (err) {
      const axiosErr = err as AxiosError<Record<string, unknown>>;
      const detail = axiosErr.response?.data?.non_field_errors
        ?? axiosErr.response?.data?.detail;
      const message = Array.isArray(detail) ? detail[0] : detail;
      return {
        success: false,
        error: typeof message === 'string' ? message : 'Correo o contraseña incorrectos',
      };
    }
  },

  register: async (params: RegisterParams) => {
    try {
      const { data } = await api.post<LoginResponse>('/auth/register/', params);

      const user = mapUser(data.user);
      const accessToken = data.tokens.access;

      Cookies.set('kore_token', accessToken, { expires: 7 });
      Cookies.set('kore_refresh', data.tokens.refresh, { expires: 7 });
      Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });

      set({ user, accessToken, isAuthenticated: true });
      return { success: true };
    } catch (err) {
      const axiosErr = err as AxiosError<Record<string, unknown>>;
      const responseData = axiosErr.response?.data;
      let message = 'Error al registrar la cuenta';
      if (responseData) {
        const firstError = Object.values(responseData)[0];
        if (Array.isArray(firstError)) {
          message = firstError[0] as string;
        } else if (typeof firstError === 'string') {
          message = firstError;
        }
      }
      return { success: false, error: message };
    }
  },

  logout: () => {
    clearAuthCookies();
    if (typeof window !== 'undefined') {
      sessionStorage.removeItem('kore_reminder_dismissed');
    }
    set({ user: null, accessToken: null, isAuthenticated: false });
  },

  hydrate: () => {
    if (get().hydrated) {
      return;
    }

    const currentState = get();
    if (currentState.isAuthenticated && currentState.accessToken && currentState.user) {
      set({ hydrated: true });
      return;
    }

    const token = Cookies.get('kore_token');
    const userStr = Cookies.get('kore_user');

    if (!token || !userStr) {
      set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      return;
    }

    try {
      JSON.parse(userStr) as User;
    } catch {
      clearAuthCookies();
      set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      return;
    }

    set({ hydrated: false });

    void api.get<ProfileResponse>('/auth/profile/', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(({ data }) => {
        const user = mapUser(data.user);
        Cookies.set('kore_user', JSON.stringify(user), { expires: 7 });
        set({ user, accessToken: token, isAuthenticated: true, hydrated: true });
      })
      .catch(() => {
        clearAuthCookies();
        set({ user: null, accessToken: null, isAuthenticated: false, hydrated: true });
      });
  },

  clearJustLoggedIn: () => {
    set({ justLoggedIn: false });
  },
}));
