import { create } from 'zustand';
import { api } from '@/lib/services/http';
import Cookies from 'js-cookie';

// ----------------------------------------------------------------
// Types
// ----------------------------------------------------------------

export type Trainer = {
  id: number;
  user_id: number;
  first_name: string;
  last_name: string;
  email: string;
  specialty: string;
  bio: string;
  location: string;
  session_duration_minutes: number;
};

export type Slot = {
  id: number;
  trainer_id: number | null;
  starts_at: string;
  ends_at: string;
  is_active: boolean;
  is_blocked: boolean;
};

export type PackageInfo = {
  id: number;
  title: string;
  sessions_count: number;
  session_duration_minutes: number;
  price: string;
  currency: string;
  validity_days: number;
};

export type Subscription = {
  id: number;
  customer_email: string;
  package: PackageInfo;
  sessions_total: number;
  sessions_used: number;
  sessions_remaining: number;
  status: 'active' | 'expired' | 'canceled';
  starts_at: string;
  expires_at: string;
  next_billing_date: string | null;
};

export type BookingData = {
  id: number;
  customer_id: number;
  package: PackageInfo;
  slot: Slot;
  trainer: Trainer | null;
  subscription_id_display: number | null;
  status: 'pending' | 'confirmed' | 'canceled';
  notes: string;
  canceled_reason: string;
  created_at: string;
  updated_at: string;
};

export type PaginatedResponse<T> = {
  count: number;
  next: string | null;
  previous: string | null;
  results: T[];
};

// ----------------------------------------------------------------
// Store
// ----------------------------------------------------------------

type BookingStep = 1 | 2 | 3;

type BookingState = {
  // Booking flow
  step: BookingStep;
  selectedDate: string | null;
  selectedSlot: Slot | null;
  trainer: Trainer | null;
  subscription: Subscription | null;
  bookingResult: BookingData | null;

  // Data lists
  trainers: Trainer[];
  slots: Slot[];
  monthSlots: Slot[];
  subscriptions: Subscription[];
  bookings: BookingData[];
  bookingDetail: BookingData | null;
  bookingsPagination: { count: number; next: string | null; previous: string | null };
  upcomingReminder: BookingData | null;

  // Loading
  loading: boolean;
  error: string | null;

  // Actions ‚Äî flow
  setStep: (step: BookingStep) => void;
  setSelectedDate: (date: string | null) => void;
  setSelectedSlot: (slot: Slot | null) => void;
  reset: () => void;

  // Actions ‚Äî API
  fetchTrainers: () => Promise<void>;
  fetchSlots: (date?: string, trainerId?: number) => Promise<void>;
  fetchMonthSlots: (trainerId?: number) => Promise<void>;
  fetchSubscriptions: () => Promise<void>;
  fetchBookings: (subscriptionId?: number, page?: number) => Promise<void>;
  fetchBookingById: (bookingId: number) => Promise<BookingData | null>;
  fetchUpcomingReminder: () => Promise<void>;
  createBooking: (payload: {
    package_id: number;
    slot_id: number;
    trainer_id?: number;
    subscription_id?: number;
    notes?: string;
  }) => Promise<BookingData | null>;
  cancelBooking: (bookingId: number, reason?: string) => Promise<BookingData | null>;
  rescheduleBooking: (bookingId: number, newSlotId: number) => Promise<BookingData | null>;
};

function authHeaders() {
  const token = Cookies.get('kore_token');
  return token ? { Authorization: `Bearer ${token}` } : {};
}

function extractErrorMessage(errData: Record<string, unknown> | undefined): string {
  if (!errData) return 'No se pudo crear la reserva.';

  // DRF detail field (string or array)
  if (errData.detail) {
    if (typeof errData.detail === 'string') return errData.detail;
    if (Array.isArray(errData.detail) && typeof errData.detail[0] === 'string') return errData.detail[0];
  }

  // DRF non_field_errors (string or array)
  if (errData.non_field_errors) {
    if (typeof errData.non_field_errors === 'string') return errData.non_field_errors;
    if (Array.isArray(errData.non_field_errors) && typeof errData.non_field_errors[0] === 'string') return errData.non_field_errors[0];
  }

  // Field-specific errors (e.g., slot_id, subscription_id)
  const fieldKeys = ['slot_id', 'subscription_id', 'package_id', 'trainer_id'];
  for (const key of fieldKeys) {
    const val = errData[key];
    if (typeof val === 'string') return val;
    if (Array.isArray(val) && typeof val[0] === 'string') return val[0];
  }

  return 'No se pudo crear la reserva.';
}

export const useBookingStore = create<BookingState>((set, get) => ({
  // Initial state
  step: 1,
  selectedDate: null,
  selectedSlot: null,
  trainer: null,
  subscription: null,
  bookingResult: null,
  trainers: [],
  slots: [],
  monthSlots: [],
  subscriptions: [],
  bookings: [],
  bookingDetail: null,
  bookingsPagination: { count: 0, next: null, previous: null },
  upcomingReminder: null,
  loading: false,
  error: null,

  // Flow actions
  setStep: (step) => set({ step }),
  setSelectedDate: (date) => set({ selectedDate: date, selectedSlot: null }),
  setSelectedSlot: (slot) => set({ selectedSlot: slot }),
  reset: () =>
    set({
      step: 1,
      selectedDate: null,
      selectedSlot: null,
      bookingResult: null,
      error: null,
    }),

  // API actions
  fetchTrainers: async () => {
    set({ loading: true, error: null });
    try {
      const { data } = await api.get<PaginatedResponse<Trainer>>('/trainers/', {
        headers: authHeaders(),
      });
      const trainers = data.results ?? data;
      set({ trainers: Array.isArray(trainers) ? trainers : [], trainer: (Array.isArray(trainers) ? trainers[0] : null) ?? null });
    } catch {
      set({ error: 'No se pudieron cargar los entrenadores.' });
    } finally {
      set({ loading: false });
    }
  },

  fetchBookingById: async (bookingId) => {
    const cached = get().bookings.find((booking) => booking.id === bookingId)
      ?? get().bookingDetail;
    if (cached?.id === bookingId) {
      set({ bookingDetail: cached });
      return cached;
    }

    set({ loading: true, error: null });
    try {
      const { data } = await api.get<BookingData>(`/bookings/${bookingId}/`, {
        headers: authHeaders(),
      });
      set({ bookingDetail: data });
      return data;
    } catch {
      set({ error: 'No se pudo cargar la reserva.', bookingDetail: null });
      return null;
    } finally {
      set({ loading: false });
    }
  },

  fetchSlots: async (date, trainerId) => {
    set({ loading: true, error: null });
    try {
      const params: Record<string, string> = {};
      if (date) params.date = date;
      if (trainerId) params.trainer = String(trainerId);
      const { data } = await api.get<PaginatedResponse<Slot>>('/availability-slots/', {
        headers: authHeaders(),
        params,
      });
      const slots = data.results ?? data;
      set({ slots: Array.isArray(slots) ? slots : [] });
    } catch {
      set({ error: 'No se pudieron cargar los horarios.' });
    } finally {
      set({ loading: false });
    }
  },

  fetchMonthSlots: async (trainerId) => {
    try {
      const params: Record<string, string> = {};
      if (trainerId) params.trainer = String(trainerId);
      const aggregated: Slot[] = [];
      let nextUrl: string | null = '/availability-slots/';
      let isFirstRequest = true;

      while (nextUrl) {
        const response: { data: PaginatedResponse<Slot> | Slot[] } = await api.get<
          PaginatedResponse<Slot> | Slot[]
        >(nextUrl, {
          headers: authHeaders(),
          ...(isFirstRequest ? { params } : {}),
        });
        const data: PaginatedResponse<Slot> | Slot[] = response.data;

        if (Array.isArray(data)) {
          aggregated.push(...data);
          break;
        }

        const results = Array.isArray(data.results) ? data.results : [];
        aggregated.push(...results);
        nextUrl = data.next ?? null;
        isFirstRequest = false;
      }

      set({ monthSlots: aggregated });
    } catch {
      set({ monthSlots: [] });
    }
  },

  fetchSubscriptions: async () => {
    set({ loading: true, error: null });
    try {
      const { data } = await api.get<PaginatedResponse<Subscription>>('/subscriptions/', {
        headers: authHeaders(),
      });
      const subs = data.results ?? data;
      set({ subscriptions: Array.isArray(subs) ? subs : [] });
    } catch {
      set({ error: 'No se pudieron cargar las suscripciones.' });
    } finally {
      set({ loading: false });
    }
  },

  fetchBookings: async (subscriptionId, page = 1) => {
    set({ loading: true, error: null });
    try {
      const params: Record<string, string> = { page: String(page) };
      if (subscriptionId) params.subscription = String(subscriptionId);
      const { data } = await api.get<PaginatedResponse<BookingData>>('/bookings/', {
        headers: authHeaders(),
        params,
      });
      set({
        bookings: data.results,
        bookingsPagination: { count: data.count, next: data.next, previous: data.previous },
      });
    } catch {
      set({ error: 'No se pudieron cargar las reservas.' });
    } finally {
      set({ loading: false });
    }
  },

  fetchUpcomingReminder: async () => {
    try {
      const { data } = await api.get<BookingData>('/bookings/upcoming-reminder/', {
        headers: authHeaders(),
      });
      set({ upcomingReminder: data?.id ? data : null });
    } catch {
      set({ upcomingReminder: null });
    }
  },

  createBooking: async (payload) => {
    set({ loading: true, error: null });
    try {
      const { data } = await api.post<BookingData>('/bookings/', payload, {
        headers: authHeaders(),
      });
      set({ bookingResult: data, step: 3 });
      return data;
    } catch (err: unknown) {
      const errData = (err as { response?: { data?: Record<string, unknown> } }).response?.data;
      const msg = extractErrorMessage(errData);
      set({ error: msg });
      return null;
    } finally {
      set({ loading: false });
    }
  },

  cancelBooking: async (bookingId, reason) => {
    set({ loading: true, error: null });
    try {
      const { data } = await api.post<BookingData>(
        `/bookings/${bookingId}/cancel/`,
        reason ? { canceled_reason: reason } : {},
        { headers: authHeaders() },
      );
      if (get().bookingDetail?.id === bookingId) {
        set({ bookingDetail: data });
      }
      return data;
    } catch (err: unknown) {
      const msg =
        (err as { response?: { data?: { detail?: string } } }).response?.data?.detail ??
        'No se pudo cancelar la reserva.';
      set({ error: typeof msg === 'string' ? msg : 'No se pudo cancelar la reserva.' });
      return null;
    } finally {
      set({ loading: false });
    }
  },

  rescheduleBooking: async (bookingId, newSlotId) => {
    set({ loading: true, error: null });
    try {
      const { data } = await api.post<BookingData>(
        `/bookings/${bookingId}/reschedule/`,
        { new_slot_id: newSlotId },
        { headers: authHeaders() },
      );
      set({ bookingResult: data, step: 3 });
      return data;
    } catch (err: unknown) {
      const msg =
        (err as { response?: { data?: { detail?: string } } }).response?.data?.detail ??
        'No se pudo reprogramar la reserva.';
      set({ error: typeof msg === 'string' ? msg : 'No se pudo reprogramar la reserva.' });
      return null;
    } finally {
      set({ loading: false });
    }
  },
}));

'use client';

import { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

export function useTextReveal(containerRef: React.RefObject<HTMLElement | null>) {
  useEffect(() => {
    if (!containerRef.current) return;

    const ctx = gsap.context(() => {
      const elements = containerRef.current!.querySelectorAll('[data-animate]');

      elements.forEach((el) => {
        const type = el.getAttribute('data-animate');
        const delay = parseFloat(el.getAttribute('data-delay') || '0');

        gsap.set(el, { opacity: 0, y: 30 });

        const config: gsap.TweenVars = {
          opacity: 1,
          y: 0,
          duration: 0.8,
          delay,
          ease: 'power3.out',
          scrollTrigger: {
            trigger: el,
            start: 'top 80%',
            toggleActions: 'play none none none',
          },
        };

        switch (type) {
          case 'fade-up':
            break;
          case 'fade-up-slow':
            config.duration = 1.2;
            config.y = 50;
            break;
          case 'fade-left':
            gsap.set(el, { opacity: 0, x: -40, y: 0 });
            config.y = 0;
            config.x = 0;
            break;
          case 'fade-right':
            gsap.set(el, { opacity: 0, x: 40, y: 0 });
            config.y = 0;
            config.x = 0;
            break;
          case 'scale-in':
            gsap.set(el, { opacity: 0, scale: 0.9, y: 0 });
            config.y = 0;
            config.scale = 1;
            break;
          case 'split-text':
            config.duration = 1;
            config.y = 40;
            config.ease = 'power4.out';
            break;
          case 'stagger-children':
            gsap.set(el, { opacity: 1, y: 0 });
            const children = el.children;
            gsap.set(children, { opacity: 0, y: 25 });
            gsap.to(children, {
              opacity: 1,
              y: 0,
              duration: 0.6,
              stagger: 0.1,
              delay,
              ease: 'power3.out',
              scrollTrigger: {
                trigger: el,
                start: 'top 80%',
                toggleActions: 'play none none none',
              },
            });
            return;
        }

        gsap.to(el, config);
      });
    }, containerRef);

    return () => ctx.revert();
  }, [containerRef]);
}

export function useHeroAnimation(containerRef: React.RefObject<HTMLElement | null>) {
  useEffect(() => {
    if (!containerRef.current) return;

    const ctx = gsap.context(() => {
      const tl = gsap.timeline({ defaults: { ease: 'power3.out' } });

      const badge = containerRef.current?.querySelector('[data-hero="badge"]');
      const heading = containerRef.current?.querySelector('[data-hero="heading"]');
      const subtitle = containerRef.current?.querySelector('[data-hero="subtitle"]');
      const body = containerRef.current?.querySelector('[data-hero="body"]');
      const cta = containerRef.current?.querySelector('[data-hero="cta"]');
      const stats = containerRef.current?.querySelector('[data-hero="stats"]');
      const image = containerRef.current?.querySelector('[data-hero="image"]');

      if (badge) tl.from(badge, { opacity: 0, y: 20, duration: 0.6, delay: 0.2 });
      if (heading) tl.from(heading, { opacity: 0, y: 40, duration: 0.8 }, badge ? '-=0.3' : undefined);
      if (subtitle) tl.from(subtitle, { opacity: 0, y: 30, duration: 0.7 }, '-=0.4');
      if (body) tl.from(body, { opacity: 0, y: 25, duration: 0.7 }, '-=0.4');
      if (cta) tl.from(cta, { opacity: 0, y: 20, duration: 0.6 }, '-=0.3');
      if (stats) tl.from(stats, { opacity: 0, y: 20, duration: 0.6 }, '-=0.3');
      if (image) tl.from(image, { opacity: 0, scale: 0.85, duration: 1.2, ease: 'power2.out' }, '-=0.8');
    }, containerRef);

    return () => ctx.revert();
  }, [containerRef]);
}

'use client';

import { useRef, useEffect } from 'react';
import Link from 'next/link';
import { useAuthStore } from '@/lib/stores/authStore';
import { useBookingStore } from '@/lib/stores/bookingStore';
import { useHeroAnimation } from '@/app/composables/useScrollAnimations';
import type { Subscription } from '@/lib/stores/bookingStore';

const STATUS_BADGE: Record<string, { label: string; className: string }> = {
  active: { label: 'Activo', className: 'bg-green-100 text-green-700' },
  expired: { label: 'Vencido', className: 'bg-amber-100 text-amber-700' },
  canceled: { label: 'Cancelado', className: 'bg-red-100 text-red-700' },
};

function SubscriptionCard({ sub }: { sub: Subscription }) {
  const badge = STATUS_BADGE[sub.status] ?? STATUS_BADGE.active;
  const expiresAt = new Date(sub.expires_at);

  return (
    <Link
      href={`/my-programs/program/${sub.id}`}
      className="block bg-white/60 backdrop-blur-sm rounded-2xl p-6 border border-kore-gray-light/50 hover:border-kore-red/30 hover:shadow-sm transition-all duration-200"
    >
      <div className="flex items-start justify-between gap-4 mb-4">
        <h3 className="font-heading text-lg font-semibold text-kore-gray-dark">
          {sub.package.title}
        </h3>
        <span className={`flex-shrink-0 px-3 py-1 rounded-full text-xs font-semibold ${badge.className}`}>
          {badge.label}
        </span>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <p className="text-xs text-kore-gray-dark/40 uppercase tracking-widest mb-1">Sesiones</p>
          <p className="text-sm font-medium text-kore-gray-dark">
            {sub.sessions_used} / {sub.sessions_total}
            <span className="text-kore-gray-dark/40"> usadas</span>
          </p>
        </div>
        <div>
          <p className="text-xs text-kore-gray-dark/40 uppercase tracking-widest mb-1">Restantes</p>
          <p className="text-sm font-medium text-kore-red">{sub.sessions_remaining}</p>
        </div>
        <div>
          <p className="text-xs text-kore-gray-dark/40 uppercase tracking-widest mb-1">Vencimiento</p>
          <p className="text-sm font-medium text-kore-gray-dark">
            {expiresAt.toLocaleDateString('es-CO', { day: 'numeric', month: 'short', year: 'numeric' })}
          </p>
        </div>
      </div>

      {/* Progress bar */}
      <div className="mt-4 h-2 bg-kore-gray-light/40 rounded-full overflow-hidden">
        <div
          className="h-full bg-kore-red rounded-full transition-all duration-500"
          style={{ width: `${sub.sessions_total > 0 ? (sub.sessions_used / sub.sessions_total) * 100 : 0}%` }}
        />
      </div>
    </Link>
  );
}

export default function MySessionsPage() {
  const { user } = useAuthStore();
  const sectionRef = useRef<HTMLElement>(null);
  useHeroAnimation(sectionRef);

  const { subscriptions, loading, fetchSubscriptions } = useBookingStore();

  useEffect(() => {
    fetchSubscriptions();
  }, [fetchSubscriptions]);

  if (!user) {
    return (
      <section className="min-h-screen bg-kore-cream flex items-center justify-center">
        <div className="animate-spin h-8 w-8 border-2 border-kore-red border-t-transparent rounded-full" />
      </section>
    );
  }

  return (
    <section ref={sectionRef} className="min-h-screen bg-kore-cream">
      <div className="w-full px-6 md:px-10 lg:px-16 pt-8 pb-16">
        {/* Header */}
        <div data-hero="badge" className="mb-8">
          <h1 className="font-heading text-2xl md:text-3xl font-semibold text-kore-gray-dark">
            Mis Programas
          </h1>
        </div>

        {/* Loading */}
        {loading && (
          <div className="flex justify-center py-16">
            <div className="animate-spin h-8 w-8 border-2 border-kore-red border-t-transparent rounded-full" />
          </div>
        )}

        {/* Programs list */}
        {!loading && subscriptions.length > 0 && (
          <div data-hero="heading" className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
            {subscriptions.map((sub) => (
              <SubscriptionCard key={sub.id} sub={sub} />
            ))}
          </div>
        )}

        {/* Empty state */}
        {!loading && subscriptions.length === 0 && (
          <div data-hero="heading" className="flex flex-col items-center justify-center py-24 text-center">
